import {EditorView} from "@codemirror/view"

export const myTheme = EditorView.theme({
    "&": {
      color: "white",
      backgroundColor: "#111",
      height: "300px",
      width: "500px"
    },
    ".cm-content": {
      caretColor: "#000",
      margin: "2px"
    },
    "&.cm-focused .cm-cursor": {
      borderLeftColor: "#fff"
    },
    "&.cm-focused .cm-selectionBackground, ::selection": {
      backgroundColor: "#034"
    },
    ".cm-gutters": {
      backgroundColor: "#333",
      color: "#eee",
      border: "none"
    },
    ".cm-lineWrapping": {
        whiteSpace: "break-spaces",
        overflowWrap: "null"
    },
  }, {dark: true})
