import {EditorView} from "@codemirror/view"

export let myTheme = EditorView.theme({
    "&": {
      color: "yellow",
      backgroundColor: "#222"
    },
    ".cm-content": {
      caretColor: "#000"
    },
    "&.cm-focused .cm-cursor": {
      borderLeftColor: "#fff"
    },
    "&.cm-focused .cm-selectionBackground, ::selection": {
      backgroundColor: "#034"
    },
    ".cm-gutters": {
      backgroundColor: "#333",
      color: "#eee",
      border: "none"
    }
  }, {dark: true})
