import {EditorView} from "@codemirror/view"

export const myTheme = EditorView.theme({
    "&": {
      color: "white",
      backgroundColor: "#111",
      height: "300px",
      width: "200px"
    },
    ".cm-content": {
      caretColor: "#000"
    },
    "&.cm-focused .cm-cursor": {
      borderLeftColor: "#fff"
    },
    "&.cm-focused .cm-selectionBackground, ::selection": {
      backgroundColor: "#034"
    },
    ".cm-gutters": {
      backgroundColor: "#333",
      color: "#eee",
      border: "none"
    },
    ".cm-lineWrapping": {
        whiteSpace: "pre-wrap",
        overflowWrap: "scroll"
    },
}, {dark: true})
