import React, { Component, createRef } from 'react'
import { basicSetup, EditorState, EditorView } from '@codemirror/basic-setup';
import { python } from '@codemirror/lang-python';
import { java } from '@codemirror/lang-java';
import { myTheme } from './Theme';
import { myHighlightStyle } from './Highlight';


export default class Editor extends Component {
    constructor(props) {
        super(props);
        this.codeMirror = createRef();
    }

    componentDidMount(language) {
        let lang;
        if (language === 'python') {
            lang = python();
        }
        else if (language === 'java') {
            lang = java();
        }
        this.editor = new EditorView({
            state: EditorState.create({
                doc: this.props.content,
                extensions: [
                    basicSetup,
                    python(),
                    myTheme,
                    myHighlightStyle
                ]
            })
        })
    }
    
    render() {
		if (this.editor) {
			this.editor.setState(EditorState.create({doc: this.props.content}));
		}

		return (
			<div ref={this.codeMirror} className="editor"></div>
		);
	}
}
