import {EditorView} from "@codemirror/view"

export let myTheme = EditorView.theme({
    "&": {
      color: "yellow",
      backgroundColor: "#000"
    },
    ".cm-content": {
      caretColor: "#"
    },
    "&.cm-focused .cm-cursor": {
      borderLeftColor: "#0e9"
    },
    "&.cm-focused .cm-selectionBackground, ::selection": {
      backgroundColor: "#074"
    },
    ".cm-gutters": {
      backgroundColor: "#045",
      color: "#ddd",
      border: "none"
    }
  }, {dark: true})
