import {tags, HighlightStyle} from "@codemirror/highlight"

export const myHighlightStyle = HighlightStyle.define([
    {
      tag: tags.keyword, 
      color: "#E6B0AA"
    },
    {
        tag: tags.comment, 
        color: "#aaa", 
        fontStyle: "italic"
    },
    {
        tag: tags.string,
        color: "#D2B4DE"
    },
    {
        tag: tags.name,
        color: "#F5CBA7"
    },
    {
        tag: tags.bracket,
        color: "#F5CBA7"
    },
    {
        tag: tags.literal ,
        color: "#A9CCE3"
    }
])
