import {tags, HighlightStyle} from "@codemirror/highlight"

export const myHighlightStyle = HighlightStyle.define([
    {
      tag: tags.keyword, 
      color: "yellow"
    },
    {
        tag: tags.comment, 
        color: "#aaa", 
        fontStyle: "italic"
    },
    {
        tag: tags.string,
        color: "aqua"
    },
    {
        tag: tags.function,
        color: "orange"
    }
])
